sourcesContent:
const btnLog = async () => {
  try {
    const formlogin = document.getElementById("form-login");
    formlogin.onsubmit = async (e) => {
      e.preventDefault();
      const formdata = new FormData(formlogin);
      const data = Object.fromEntries(formdata.entries());
      console.log(formdata);

      const response = await fetch("/login", {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.status < 400) {
        const responseData = await response.text();
        localStorage.setItem("jwt", `Bearer ${responseData}`);
        location.reload();
      } else {
        formlogin.reset();
        return alert("The username or password is in incorrect");
      }
    };
  } catch (error) {
    console.log("Aun no se ha ingresado a este formulario");
  }
};

const btndelete = async () => {
  try {
    const formlogin = document.getElementById("form-delete");
    formlogin.onsubmit = async (e) => {
      e.preventDefault();
      const id = document.getElementById("acticuloId").value;
      const formdata = new FormData(formlogin);
      const data = Object.fromEntries(formdata.entries());
      console.log(formdata);

      const response = await fetch("/login", {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (response.status < 400) {
        const responseData = await response.text();
        localStorage.setItem("jwt", `Bearer ${responseData}`);

        await fetch(`/deleteCharacter/${id}`, {
          method: "DELETE",
          body: JSON.stringify(data),
        });
        location.reload();
      } else {
        formlogin.reset();
        return alert("The username or password is in incorrect");
      }
    };
  } catch (error) {
    console.log("Aun no se ha ingresado a este formulario");
  }
};

export const functi = (tex) => {
  let array = [];
  for (i in tex) {
    array.push(tex[i]);
  }
  for (let i = 0; i < tex.length; i++) {
    if (tex[i] == ",") {
      array[i] = "\n";
    }
  }
  return array.join("");
};

window.onload = () => {
  btnLog();
  btndelete();
};

